// Header for CBalloonApp class#pragma once#ifndef BALLOON_SUPPORT    #if TARGET_OS_MAC && !TARGET_API_MAC_CARBON        #define BALLON_SUPPORT 1    #else        #define BALLON_SUPPORT 0    #endif#endif // def BALLOON_SUPPORT#include <LDocApplication.h>class LPane;class CBalloonApp : public LDocApplication {public:					CBalloonApp();						static LPane*	sHelpPane;				// Cached pane for balloon display	static LPane*	sLastHelpLine;			// Cached pane for help line display	static LPane*	sHelpLineDisplay;		// pane into which help line is placed	static UInt32	sLastMouseMoveTick;		// when did the mouse last move?	static bool		sMouseLingerActive;		// is a balloon being shown because of a lingering mouse?	static bool		sMouseLingerEnabled;//	static Point	sLastMousePoint;		// where was the mouse when it last moved?	protected:	virtual void	AdjustCursor(const EventRecord &inMacEvent); // overridden from LEventDispatcherprivate:	Boolean			mHasBalloonHelp;		// Flag for Gestalt};