// ProductInformation.h// Defines for Accessing Product Information// Tuesday, April 30, 1996// Copyright © 1994, Teknosys, Inc.#ifndef	__PRODUCTINFORMATION__#define	__PRODUCTINFORMATION__// ResTypes#define	kVersionResType		'vers'#define	kProductInfoResType	'STR#'#define	kProductDescResType	'TEXT'// Product Information Resourcesenum{	kFileVersionID					= 1,	// ID of file 'vers' resource.	kProductVersionID				= 2,	// ID of product 'vers' resource.	kProductInfoID					= 1,	// ID of product info 'STR#' resource.		kProductName				= 1,	// Name of product this file is from.		kProductFileName			= 2,	// Actual file name of this file.		kProductReleaseYear		= 3,	// Year(s) this product was released.		kProductWebSite			= 4,	// Where to get support for this file.		kProductFTPDirectory		= 5,	// Where to find updates of this file.	kVendorInfoID					= 2,	// ID of vendor info 'STR#' resource.		kVendorAuthor				= 1,	// Name of person who created this file.		kVendorCompany				= 2,	// Company who supports this file.		kVendorAddress				= 3,	// Company or author’s address.		kVendorTelephone			= 4,	// Company or author’s support number.		kVendorFax					= 5,	// Company or author’s fax number.		kVendorAppleLink			= 6,	// This field is no longer supported by VersionEdit.		kVendoreWorld				= 6,	// AppleLink was changed to eWorld, which is now defunct.		kVendorAmericaOnline		= 7,	// Company or author’s AOL screen name.		kVendorCompuServe			= 8,	// Company or author’s CIS user ID number.		kVendorInternet			= 9,	// Company or author’s internet email address.		kVendorWebSite				= 10,	// Company or author’s main web-site address.		kVendorFTPDirectory		= 11,	// Company or author’s main FTP directory address.	kProductDescriptionID		= 1	// ID of product description 'TEXT' resource.};#ifdef __cplusplusextern "C" {#endif// Function prototypesextern long GetVersionNumber(short versionID);extern short GetVersionNumberString(short versionID, std::string& str);extern short GetVersionMessage(short versionID, std::string& message);#ifdef __cplusplus}#endif#endif